mapper.update:
	update tbl_area set
 	area_name = #{area_name}
	where area_no = #{area_no}

mapper.select:
select * from tbl_area_theater
where area_no = #{area_no}
order by area_theater_no asc

mapper.update:
	update tbl_area_theater set
 	area_theater_name = #{area_theater_name}
	where area_theater_no = #{area_theater_no}
